import React from 'react';
import { white, primary, secondary, warning } from '../colors';

export default function InlineStyle(props) {
  const buttonStyles = {
    backgroundColor: props.primary ?  primary : warning ,
    color: "#fff",
    padding: "7px 14px",
    margin: "0 5px",
    borderRadius: "5px",
    border: "1px solid transparent",
  };
  return (
    <div>
      <h2>Inline Styles</h2>
      <button style={buttonStyles}>Primary</button>
      <button>Secondary</button>
    </div>
  );
}
